<?php    // 用户控制器namespace Home;use DB;    class UserController    {        // 登录        public function login()        {            include('./Views/Home/User/login.html');        }                // 验证登录        public function dologin()        {            // 接收数据            $uname = $_POST['uname'];            $upwd = md5($_POST['upwd']);                        // 查询            $user = (new DB('shop_users')) -> where("uname='{$uname}'")                                           -> where("upwd='{$upwd}'")                                           -> find();                        // 如果成功            if ($user) {                $_SESSION['homeFlag'] = true;                $_SESSION['userInfo'] = $user;                                if (empty($_SESSION['back'])){                    header('refresh:2;url=./index.php?m=home&c=first&a=first');                } else {                    header('refresh:2;url='.$_SESSION['back']);                    unset($_SESSION['back']);                }                die('登录成功...3秒后跳转...');                            } else {                // 如果失败                header('refresh:2;url=./index.php?m=home&c=user&a=login');                die('登录失败...3秒后跳转...');            }                                }                        // 退出登录        public function logout()        {            $_SESSION['homeFlag'] = false;            header('refresh:2;url=./index.php?m=home&c=first&a=first');            die('正在退出...3秒后跳转...');        }    }