<?php    function __autoload($className)    {        $className = str_replace('\\', '/', $className);        $c = './Controllers/'.$className.'.php';        if (file_exists($c)) {            include($c);            return;        }                $c = './Model/'.$className.'.php';        if (file_exists($c)) {            include($c);            return;        }            $c = './Public/'.$className.'.php';        if (file_exists($c)) {            include($c);            return;        }                die('类文件没有找到:'.$className);    }